import React, { useState, useEffect } from 'react';
import './Calendar.css'; // Убедитесь, что CSS подключен правильно

const Calendar = ({ selectedDate }) => {
    const [currentDate, setCurrentDate] = useState(new Date(selectedDate || new Date()));
    const startDay = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay();
    const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
    const days = [];

    useEffect(() => {
        setCurrentDate(new Date(selectedDate || new Date())); // Обновлять текущую дату при изменении selectedDate
    }, [selectedDate]);

    const isSelectedDate = (day) => {
        const checkDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
        return selectedDate && checkDate.toISOString().slice(0, 10) === new Date(selectedDate).toISOString().slice(0, 10);
    };

    for (let i = 0; i < startDay; i++) {
        days.push(<div key={`empty-${i}`} className="calendar-day empty"></div>);
    }
    for (let i = 1; i <= daysInMonth; i++) {
        days.push(
            <div key={i} className={`calendar-day ${isSelectedDate(i) ? 'selected' : ''}`}>
                {i}
            </div>
        );
    }

    return (
        <div className="calendar-container">
            {days}
        </div>
    );
};

export default Calendar;
