import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import CreateInvitation from './components/CreateInvitation';
import EditInvitation from './components/EditInvitation';
import Login from './components/Login';
import InvitationView from './components/InvitationView';

const App = () => {
  const [invitations, setInvitations] = useState([]);

  return (
    <Router>
      <div>
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/create" element={<CreateInvitation setInvitations={setInvitations} />} />
          <Route path="/invitation/:id" element={<InvitationView invitations={invitations} />} />
          <Route path="/edit/:id" element={<EditInvitation invitations={invitations} setInvitations={setInvitations} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
