import React, { useEffect } from 'react';

const YandexMap = ({ location }) => {
  useEffect(() => {
    let map;

    function initMap() {
      if (window.ymaps && location) {
        window.ymaps.ready(() => {
          map = new window.ymaps.Map("map", {
            center: [55.76, 37.64], // Подставьте нужные координаты для начального центра карты
            zoom: 7
          });

          window.ymaps.geocode(location).then(function (res) {
            const firstGeoObject = res.geoObjects.get(0);
            map.geoObjects.add(firstGeoObject);
            const bounds = firstGeoObject.properties.get('boundedBy');
            map.setBounds(bounds, { checkZoomRange: true });
          }).catch(error => console.error('Ошибка геокодирования: ', error));
        });
      }
    }

    initMap();

    return () => {
      if (map) {
        map.destroy(); // Уничтожаем карту при размонтировании компонента
      }
    };
  }, [location]);

  return <div id="map" style={{ width: "100%", height: "400px" }}></div>;
};

export default YandexMap;
