import React, { useEffect, useRef, useState } from 'react';
import './Modal.css';

const Modal = ({ showModal, handleClose, children, musicSrc }) => {
  const audioRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);

  useEffect(() => {
    if (showModal && audioRef.current) {
      audioRef.current.play();
    }
  }, [showModal]);

  const toggleMute = () => {
    if (audioRef.current) {
      audioRef.current.muted = !audioRef.current.muted;
      setIsMuted(!isMuted);
    }
  };

  const handleOverlayClick = (event) => {
    if (event.target.className.includes('modal-overlay')) {
      handleClose();
    }
  };

  return (
    showModal && (
      <div className="modal-overlay" onClick={handleOverlayClick}>
        <div className="modal-content">
          <img
            src={isMuted ? 
              <svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" viewBox="0 0 24 24"><g fill="none" stroke="#A48768"><path d="M3.158 13.93a3.75 3.75 0 0 1 0-3.86a1.5 1.5 0 0 1 .993-.7l1.693-.339a.45.45 0 0 0 .258-.153L8.17 6.395c1.182-1.42 1.774-2.129 2.301-1.938S11 5.572 11 7.42v9.162c0 1.847 0 2.77-.528 2.962c-.527.19-1.119-.519-2.301-1.938L6.1 15.122a.45.45 0 0 0-.257-.153L4.15 14.63a1.5 1.5 0 0 1-.993-.7Z"/><path stroke-linecap="round" d="M15.536 8.464a5 5 0 0 1 .027 7.044"/></g></svg> 
              : 
              <svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" viewBox="0 0 24 24"><g fill="none" stroke="#A48768"><path d="M3.158 13.93a3.75 3.75 0 0 1 0-3.86a1.5 1.5 0 0 1 .993-.7l1.693-.339a.45.45 0 0 0 .258-.153L8.17 6.395c1.182-1.42 1.774-2.129 2.301-1.938S11 5.572 11 7.42v9.162c0 1.847 0 2.77-.528 2.962c-.527.19-1.119-.519-2.301-1.938L6.1 15.122a.45.45 0 0 0-.257-.153L4.15 14.63a1.5 1.5 0 0 1-.993-.7Z"/><path stroke-linecap="round" d="M15.536 8.464a5 5 0 0 1 .027 7.044"/></g></svg>}
            className="mute-icon"
            onClick={toggleMute}
            alt="Sound Control"
          />
          {children}
          <audio ref={audioRef} src={musicSrc} loop />
        </div>
      </div>
    )
  );
};

export default Modal;
